import React from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import ButtonBase from '@material-ui/core/ButtonBase';
import Button from '@material-ui/core/Button';


const styles = theme => ({
  root: {
    flexGrow: 1,
  },
  mainStyle: {
    paddingRight: theme.spacing.unit * 8,
    paddingBottom: theme.spacing.unit * 2,
    paddingLeft: theme.spacing.unit * 3,
    marginBottom: 5,
    maxWidth: '100%',
  },
});

class MainContent extends React.Component{
    render(){
        const { classes, theme } = this.props;

  return (
    <div className={classes.mainStyle}>
      <Grid container spacing={16}>
        <Grid item xs={12} sm container>
          <Grid item xs container direction="column" spacing={16}>
            <Grid item xs>
              <Typography gutterBottom variant="subtitle1">
                {this.props.maincontent[0].title}
              </Typography>
              <Typography gutterBottom color="textSecondary">
              {this.props.maincontent[0].date}
              </Typography>
              <Typography variant="body2">
              Hello, Javascript & Node.js Ninjas!
              </Typography>
              <Typography variant="body2" gutterBottom>
                Get ready for our montly meetup JakartaJS! This will be our fifth meetup of 2018!. The meetup format will contain some short stories and trchnical tyalks. If you have a short announcement you'd like to share with the audience, you may do so during open mic announcements.
              </Typography>
              <Typography variant="body2" gutterBottom>
              Remember to bring a photo ID to get through building security.
             </Typography>
             <Typography variant="body2" gutterBottom>
              -----
             </Typography>
             <Typography variant="body2" gutterBottom>
              See You There!
             </Typography>
             <Typography variant="body2" gutterBottom>
              Best, Hengki, Giovanni, Sofian, Riza, Agung The JakartaJS Organizers
             </Typography>
            </Grid>
          </Grid>
          
        </Grid>
      </Grid>
    </div>  
    );
    }
}

export default withStyles(styles, { withTheme: true })(MainContent);
